import React, { Component } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import Popup from 'react-popup';
import PrimarySearchAppBar from './Primary_searchbar';

class Settings extends Component {  
    constructor(props) {
        super(props);
        this.state = { 
            username: '',
            preferences: ''            
        };
    }
    redirect = () => {
        this.props.history.push('/')
    }
    handleErrors = (input_error) => {
        alert('action failed', input_error.response.data);
    };
    render() {
        //const {username, email, password} = this.state
        return (
            <div className="container mt-5">
                <PrimarySearchAppBar/>
                <div className="row">
                    <div className="col-sm-12 col-lg-6 offset-lg-3">
                        <h1 className="font-weight-normal mb-5">
                        Settings
                        </h1>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label htmlFor="image_uploader_Name">Image uploader name</label>
                                <input
                                type="text"
                                name="uploader_name"
                                id="image_uploader_Name"
                                className="form-control"
                                required
                                onChange={this.onChange}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="image_Categories">Categories</label>
                                <input
                                type="text"
                                name="category"
                                id="image_Categories"
                                className="form-control"
                                required
                                onChange={this.onChange}
                                />
                                <small id="categoriesHelp" className="form-text text-muted">
                                Separate each category with a comma.
                                </small>
                            </div>
                            <label htmlFor="image_Access_level">access_level</label>
                            <textarea
                                className="form-control"
                                id="image_Access_level"
                                name="access_level"
                                rows="5"
                                required
                                onChange={this.onChange}
                            />
                            <label htmlFor="image_image_url">image url</label>
                            <textarea
                                className="form-control"
                                id="image_image_url"
                                name="image"
                                rows="5"
                                required
                                onChange={this.onChange}
                            />
                            <button type="submit" className="btn custom-button mt-3">
                                Create Image
                            </button>
                            <Link to="/images" className="btn btn-link mt-3">
                                Back to images
                            </Link>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
export default Settings;
